public class TesteOperadoresIncrementoDecremento1 {


	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Pré-fixado");
		int x =  5;
		
		System.out.println("x = " + x);
		System.out.println("++x = " + ++x);
		System.out.println("x = " + x);
		System.out.println("x = " + --x);
		System.out.println("x = " + x);
		System.out.println("Pós-fixado");
		System.out.println("x++ = " + x++);
		System.out.println("x = " + x);
		System.out.println("x-- = " + x--);
		System.out.println("x = " + x);
	}
}
